def main():
    try:
        # Просим пользователя ввести число
        user_input = input("Введите число в диапазоне [-100; 100]: ")
        number = float(user_input)  # Преобразуем ввод в число (float на случай дробных чисел)

        # Проверяем, входит ли число в диапазон [-100; 100]
        if number < -100 or number > 100:
            print("Число не входит в диапазон [-100; 100]")
        elif number < -50:
            print("Число меньше -50")
        elif number == -50:
            print("Число равно -50")
        elif -50 < number < 0:
            print("Число меньше 0, но больше -50")
        elif 0 < number < 50:
            print("Число больше 0, но меньше 50")
        elif number == 50:
            print("Число равно 50")
        elif number > 50:
            print("Число больше 50")
        else:
            # Если число равно 0, оно не попадает ни в один из предыдущих условий
            print("Число равно 0")
    
    except ValueError:
        print("Ошибка: Пожалуйста, введите числовое значение.")

# Запуск функции main() при непосредственном запуске скрипта
if __name__ == "__main__":
    main()