def main():
    # 1. Печать чисел от 0 до 10 с использованием цикла for
    print("Числа от 0 до 10 с использованием цикла for:")
    for i in range(11):
        print(i, end=' ')  # Печатаем число и добавляем пробел
    print()


    print("\nЧисла от 0 до 10 с использованием цикла while:")
    # 2. Печать чисел от 0 до 10 с использованием цикла while
    i = 0
    while i <= 10:
        print(i, end=' ')  # Печатаем число и добавляем пробел
        i += 1
    print()
    
    # 3. Создание строковой переменной с ФИО
    fio = "Паутов Глеб Алексеевич"  
    
    # 4. Печать каждой буквы ФИО с использованием цикла for
    print("\nПечать каждой буквы вашего ФИО:")
    for char in fio:
        print(char, end='')  # Печатаем каждую букву без пробелов
    print()
    
    # 5. Создание списка с именами друзей
    friends = ["Максим", "Антон", "Семен", "Эля"]
    
    # 6. Печать имен друзей с использованием цикла for
    print("\nПечать имен ваших друзей:")
    for friend in friends:
        print(friend)
    
    # 7. Создание словаря с именами друзей и их датами рождения
    friends_birthdays = {
        "Алексей": "15.06.2002",  # Лето
        "Мария": "10.12.2001",    # Зима
        "Петр": "25.03.2000",     # Весна
        "Ольга": "30.08.2003"     # Лето
    }
    
    # 8. Печать имен друзей, родившихся летом или зимой, и их даты рождения
    print("\nДрузья, родившиеся летом или зимой:")
    for friend, birthday in friends_birthdays.items():
        # Извлечение месяца из даты рождения
        month = int(birthday.split(".")[1])
        
        # Проверка на лето (6, 7, 8) или зиму (12, 1, 2)
        if month in [6, 7, 8]:
            season = "летом"
            print(f"{friend} родился {season}: {birthday}")
        elif month in [12, 1, 2]:
            season = "зимой"
            print(f"{friend} родился {season}: {birthday}")

# Запуск функции main() при непосредственном запуске скрипта
if __name__ == "__main__":
    main()